SELECT E.FIRSTNAME, E.LASTNAME, E.EMAIL, E.OFFICECODE FROM EMPLOYEES E INNER JOIN OFFICES O ON E.OFFICECODE = O.OFFICECODE;
SELECT  P.PRODUCTNAME, P.PRODUCTVENDOR, P.PRODUCTLINE FROM PRODUCTS P LEFT JOIN PRODUCTLINES PL ON P.PRODUCTLINE = PL.PRODUCTLINE;
SELECT O.ORDERDATE, O.SHIPPEDDATE, O.STATUS, O.CUSTOMERNUMBER FROM CUSTOMERS C RIGHT JOIN  ORDERS O ON C.CUSTOMERNUMBER = O.CUSTOMERNUMBER LIMIT 10;